mapper.select:
	SELECT
	*
FROM
	t_user

	
		and user_id=#{params.userId}
	
	
	
		and user_id=#{params.userId}
	
	
	
		and mobile=#{params.mobile}
	
	
	
		and nickName=#{params.nickName}
	
	
	
		and gender=#{params.gender}
	
	
	
		and user_type=#{params.userType}
	
	and deleted=0

limit #{startNum},#{pageSize}

